--Creación de tabla departamento y cargado de datos
CREATE TABLE IF NOT EXISTS departamentos (
	id_departamento INT PRIMARY KEY,
	nombre_departamento VARCHAR(50) NOT NULL
);


--Creación de tabla de nodos y cargado de datos
CREATE TABLE IF NOT EXISTS nodos (
	id_nodo INT PRIMARY KEY,
	nombre_nodo VARCHAR(50) NOT NULL
);




--Creación de tabla localidad y cargado de datos
CREATE TABLE IF NOT EXISTS localidades (
	id INT(4) AUTO_INCREMENT PRIMARY KEY,
	nombre_localidad VARCHAR(50) NOT NULL,
	id_departamento INT,
	id_nodo INT,
	categoria VARCHAR(50),
	codigo_postal VARCHAR(8)
);



